def bite(s: str, k: int, start_idx: int) -> int:
    consumed = set()
    for idx in range(start_idx, len(s)):
        if s[idx] in consumed:
            continue
        if len(consumed) == k:
            return idx
        consumed.add(s[idx])
    return len(s)


def count_partitions(s: str, k: int) -> int:
    idx = 0
    part = 0

    while idx < len(s):
        idx = bite(s, k, idx)
        part += 1

    return part


ALPHABET = [chr(x) for x in range(ord('a'), ord('z') + 1)]


class Solution:
    def maxPartitionsAfterOperations(self, s: str, k: int) -> int:
        # try to change ANY of the char
        best = count_partitions(s, k)
        orig = s
        for idx in range(len(s)):
            for replacement in ALPHABET:
                s = orig[:idx] + replacement + orig[idx + 1:]
                best = max(best, count_partitions(s, k))
        return best


