class Solution:
    def answerString(self, word: str, numFriends: int) -> str:
        if numFriends == 1:
            return word
        l = len(word) - numFriends + 1
        best = word[0]
        for idx in range(len(word)):
            candidate = word[idx:idx + l]
            if candidate > best:
                best = candidate
        return best

if __name__ == '__main__':
    x = Solution()
    print(x.answerString('a', 1))
    print(x.answerString('bif', 2))
    print(x.answerString('aann', 2))
    print(x.answerString('', 1887))
