class Solution:
    def answerString(self, word: str, numFriends: int) -> str:
        if numFriends == 1:
            return word
        best = word[0]
        for k in range(1, len(word) - numFriends + 1 + 1):
            for idx in range(0, len(word) - k + 1):
                candidate = word[idx:idx + k]
                if candidate > best:
                    best = candidate
        return best

if __name__ == '__main__':
    x = Solution()
    print(x.answerString('a', 1))
    print(x.answerString('bif', 2))
    print(x.answerString('aann', 2))
    print(x.answerString('', 1887))
